CommandUIHandler.EnabledScript:
javascript:
           function enableApprovalButton()
           {
             var items = SP.ListOperation.Selection.getSelectedItems();
             return (items.length > 0);
           }
           enableApprovalButton();

CommandUIHandler.CommandAction:
javascript:
          function selectItems()
           {           
             var selectedItems = SP.ListOperation.Selection.getSelectedItems();
              var selectedIds = '';
              var listId = SP.ListOperation.Selection.getSelectedList();
              var i = 0;
              while(i!=selectedItems.length)
              {
                selectedIds += selectedItems[i].id + ',';
                i++;
              }
              selectedIds = escape(selectedIds.substring(0,selectedIds.length-1));
              var sourceUrl = location.href;
             window.location = _spPageContextInfo.siteAbsoluteUrl + '/_layouts/15/I.BLF.WebServices/CancelWorkflow.aspx?ID=' + selectedIds + '&listId=' + listId + '&Source=' + sourceUrl;
           }           
           selectItems();
